import { IIdObject } from '@common/interfaces/id-object.interface';
import { UID } from '@common/interfaces/uid.interface';

function getFirst<T extends IIdObject>(items: T[] | undefined): T | undefined {
    if (items && items.length) {
        return items[0];
    }
    return undefined;
}

function deleteItem<T extends IIdObject>(items: T[], id: UID): T[] {
    const item = items.find((i) => i.id === id);
    if (item) {
        const idx = items.indexOf(item);
        if (idx >= 0) {
            return [...items.slice(0, idx), ...items.slice(idx + 1)];
        }
    }
    return items;
}

function replaceItem<T extends IIdObject>(items: T[], id: UID, newItem: T): T[] {
    const item = items.find((i) => i.id === id);
    if (item) {
        const idx = items.indexOf(item);
        if (idx >= 0) {
            return [...items.slice(0, idx), newItem, ...items.slice(idx + 1)];
        }
    }
    return items;
}

function unique<T>(list: T[], name: keyof T): T[] {
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    const result = list.reduce((acc: any, it: T) => {
        acc[it[name]] = it;
        return acc;
    }, {});

    return Object.values(result);
}

function reorder<T>(list: T[], startIndex: number, endIndex: number): T[] {
    const result = Array.from(list);
    const [removed] = result.splice(startIndex, 1);
    result.splice(endIndex, 0, removed);

    return result;
}

export const arrayUtils = {
    getFirst,
    unique,
    deleteItem,
    replaceItem,
    reorder,
};
