import { ITabItem } from '@common/general-ui/tabs/tabs.component';
import { ILink } from '@common/interfaces/link.interface';

export const filterVisibleLinks = (links: ILink[], filterFn?: (link: ILink) => boolean): ILink[] => {
    return links
        .filter((l) => (filterFn ? l.isVisible && filterFn(l) : l.isVisible))
        .map((link: ILink): ILink => {
            if (link.subLinks) {
                return {
                    ...link,
                    subLinks: filterVisibleLinks(link.subLinks),
                };
            }
            return link;
        });
};

export function filterVisibleTabs<T>(links: ITabItem<T>[], filterFn?: (link: ITabItem<T>) => boolean): ITabItem<T>[] {
    return links
        .filter((l) => (filterFn ? l.isVisible && filterFn(l) : l.isVisible))
        .map((link: ITabItem<T>): ITabItem<T> => {
            return link;
        });
}
