import { IInputProps, Input } from '@common/general-ui/input/input.component';
import React, { useCallback, useEffect, useState } from 'react';

export const PhoneNumber = (props: Omit<IInputProps, 'type'>): JSX.Element => {
    const [val, setVal] = useState(props.defaultValue || props.value);
    useEffect(() => {
        if (props.value !== undefined) {
            setVal(props.value);
        }
    }, [props.value]);

    useEffect(() => {
        if (props.defaultValue !== undefined) {
            setVal(props.defaultValue);
        }
    }, [props.defaultValue]);

    const handleChange = useCallback(
        (e: React.ChangeEvent<HTMLInputElement>) => {
            setVal(e.target.value);
            props.onChange && props.onChange({ ...e, target: { ...e.target, value: e.target.value } });
        },
        [props],
    );

    const formatValue = useCallback((value: string) => {
        const first = value?.length ? value[0] : '';
        const newValue = first === '+' ? first + value.replaceAll(/[^0-9]/g, '') : value.replaceAll(/[^0-9]/g, '');
        return newValue;
    }, []);

    return <Input {...props} type="text" onChange={handleChange} formatValue={formatValue} value={val} />;
};
