import { useField } from '@common/hooks/use-field.hook';
import React, { useCallback } from 'react';
import { IRadioGroupProps, RadioGroup } from './radio-group.component';
import { IRadioBlurHandler, IRadioChangeHandler } from './radio.component';

export function FormRadioGroup<TFormValues>(
    props: Omit<IRadioGroupProps<keyof TFormValues>, 'value' | 'defaultValue'>,
): JSX.Element {
    const { onChange, onBlur, getCustomErrorMessage } = props;
    const { setFieldValue, setFieldTouched, value, errorMessage } = useField<string>({
        ...props,
        getCustomErrorMessage,
    });

    const handleChange = useCallback<IRadioChangeHandler>(
        (e) => {
            onChange && onChange(e);
            setFieldValue(e.target.name);
        },
        [onChange, setFieldValue],
    );

    const handleBlur = useCallback<IRadioBlurHandler>(
        (e) => {
            onBlur && onBlur(e);
            setFieldTouched();
        },
        [onBlur, setFieldTouched],
    );

    return (
        <RadioGroup
            errorMessage={errorMessage}
            {...props}
            onChange={handleChange}
            onBlur={handleBlur}
            name={props.name as string}
            value={value}
        />
    );
}
