import { THEME } from '@common/constants/theme.constant';
import { ILink } from '@common/interfaces/link.interface';
import { Menu as AntdMenu, MenuProps } from 'antd';
import React, { ReactNode } from 'react';
import { css } from 'twin.macro';
import { MenuItem } from './menu-item.component';
import { SubMenu } from './sub-menu.component';

export * from './menu-item.component';

export interface IMenuItem extends ILink {
    icon?: ReactNode;
}

interface IProps extends MenuProps {
    className?: string;
    menuItems?: IMenuItem[];
    renderMenuItem?: (link: IMenuItem, level: number) => ReactNode;
    renderSubMenuItem?: (link: IMenuItem, children: ReactNode, level: number) => ReactNode;
}

export const Menu = ({ menuItems, renderMenuItem, renderSubMenuItem, ...rest }: IProps): JSX.Element => {
    const renderContent = (items: ILink[] | undefined, level: number): JSX.Element[] | undefined => {
        return items
            ?.filter((i) => i.isVisible)
            .map((item) => {
                const subLinks = item.subLinks?.filter((f) => f.isVisible);
                if (subLinks?.length) {
                    return renderSubMenuItem ? (
                        <React.Fragment key={item.name}>
                            {renderSubMenuItem(item, renderContent(item.subLinks, level + 1), level + 1)}
                        </React.Fragment>
                    ) : (
                        <SubMenu key={item.name} title={item.title} name={item.name}>
                            {renderContent(item.subLinks, level + 1)}
                        </SubMenu>
                    );
                }
                return renderMenuItem ? (
                    <React.Fragment key={item.name}>{renderMenuItem(item, level + 1)}</React.Fragment>
                ) : (
                    <MenuItem key={item.name}>{item.title}</MenuItem>
                );
            });
    };

    return (
        <AntdMenu css={styles.root} {...rest}>
            {renderContent(menuItems, 0)}
        </AntdMenu>
    );
};

const styles = {
    root: css`
        border-right: 0;
        && .ant-table-thead {
            background-color: ${THEME.GREEN};
            color: white;
        }
        && .ant-menu {
            border-right: 0;
        }
    `,
};
