import { useField } from '@common/hooks/use-field.hook';
import React, { useCallback } from 'react';
import { IInputLoqateChangeHandler, IInputLoqateProps, IInputLoqateValue, InputLoqate } from './input-loqate.component';

export function FormInputLoqate<TFormValues>(
    props: Omit<IInputLoqateProps<keyof TFormValues>, 'value' | 'defaultValue'>,
): JSX.Element {
    const { onChange, getCustomErrorMessage } = props;
    const {
        setFieldValue,

        value,
        errorMessage,
        setFieldTouched: setFieldTouched,
    } = useField<IInputLoqateValue>({
        ...props,
        getCustomErrorMessage,
    });

    const handleChange = useCallback<IInputLoqateChangeHandler>(
        (e) => {
            setFieldValue(e.value);
            onChange && onChange(e);
            setFieldTouched();
        },
        [onChange, setFieldTouched, setFieldValue],
    );
    return (
        <InputLoqate
            errorMessage={errorMessage}
            {...props}
            onChange={handleChange}
            name={props.name as string}
            defaultValue={value}
            value={value}
        />
    );
}
