import { useField } from '@common/hooks/use-field.hook';
import React, { useCallback } from 'react';
import { CheckboxGroup, ICheckboxGroupChangeHandler, ICheckboxGroupProps } from './checkbox-group.component';

export function FormCheckboxGroup<TFormValues>(
    props: Omit<ICheckboxGroupProps<keyof TFormValues>, 'value' | 'defaultValue'>,
): JSX.Element {
    const { onChange, getCustomErrorMessage } = props;
    const { setFieldValue, value, errorMessage } = useField<string[]>({
        ...props,
        getCustomErrorMessage,
    });

    const handleChange = useCallback<ICheckboxGroupChangeHandler>(
        (e) => {
            onChange && onChange(e);
            setFieldValue(e.value);
        },
        [setFieldValue, onChange],
    );

    return (
        <CheckboxGroup
            errorMessage={errorMessage}
            {...props}
            onChange={handleChange}
            name={props.name as string}
            value={value}
        />
    );
}
