import { StatusTag } from '@common/general-ui/tag/status-tag.component';
import { Tooltip } from '@common/general-ui/tooltip/tooltip.component';
import React from 'react';
import 'twin.macro';

export interface IValidationError<T = unknown> {
    tooltip: string;
    title: string;
    value: T;
}
export const renderValidationErrors = (errors: IValidationError[]): JSX.Element => {
    return (
        <>
            {errors.map((error) => (
                <div key={error.title} tw="p-1">
                    <Tooltip title={error.tooltip}>
                        <StatusTag status="error">{error.title}</StatusTag>
                    </Tooltip>
                </div>
            ))}
        </>
    );
};
