import { useEffect, useRef } from 'react';

export const useIsScrollEnd = ({
    next_page: haveNextPage,
    onScrollEnd,
}: {
    next_page: number | undefined;
    onScrollEnd?: () => void;
}): void => {
    const nextPageRef = useRef<number | undefined>();
    nextPageRef.current = haveNextPage;
    useEffect(() => {
        const scrollHandler = (_ev: Event): void => {
            if (nextPageRef.current) {
                const el = document.documentElement;
                if (el.offsetHeight + el.scrollTop + 1 >= el.scrollHeight) {
                    onScrollEnd && onScrollEnd();
                }
            }
        };
        window.addEventListener('scroll', scrollHandler);
        return (): void => window.removeEventListener('scroll', scrollHandler);
    }, [onScrollEnd]);
};
