import { ICollectionRequestOptions } from '@common/interfaces/collection-request-options.interface';
import { ICollection } from '@common/interfaces/collection.interface';
import { InfiniteData } from 'react-query';
import { createCollection } from './create-collection.util';

export function convertInfiniteToCollection<TData, TFilter>(
    input: InfiniteData<ICollection<TData, TFilter>> | undefined,
    request: ICollectionRequestOptions<TFilter> | undefined,
): ICollection<TData, TFilter> {
    const lastPage = input?.pages.length ? input.pages[input.pages.length - 1] : undefined;
    return createCollection<TData, TFilter>(
        input?.pages.flatMap((x) => x.list),
        lastPage?.next_page,
        lastPage?.request || request,
    );
}
