import { IBreadCrumbItem } from '@common/general-ui/breadcrumb/breadcrumb.component';
import { ILinkItem, LinkItem } from '@common/utils/make-links.util';

type IItem = ILinkItem | { url: ILinkItem; formattedName: string };
export const createBreadCrumbItems = (routes: IItem[]): IBreadCrumbItem[] => {
    return routes.map((route: IItem & { url?: ILinkItem }) => {
        return {
            link: route.url ? route.url : route,
            // TODO: maybe we should make changes in formatRouteName (make-links-util.ts)
            // not here
            title: (route as LinkItem).formattedName?.replaceAll(/-/g, ' '),
        };
    });
};
