import { THEME } from '@common/constants/theme.constant';
import { Field, IFieldProps } from '@common/general-ui/field/field.component';
import { Readonly } from '@common/general-ui/readonly.component';
import { SerializedStyles } from '@emotion/react';
import dayjs, { Dayjs } from 'dayjs';
import React, { useCallback, useMemo } from 'react';
import { css } from 'twin.macro';
import { AntDatePickerWithDayjs } from './ant-date-picker.component';

export type IDatePickerChangeHandler = (props: { name: string; value: Date | undefined; valueStr: string }) => void;

export interface IDatePickerProps<TName = string> extends IFieldProps<Date | undefined, TName> {
    showTime?: boolean;
    disableDate?: (date: Dayjs) => boolean;
    className?: string;
    onChange?: IDatePickerChangeHandler;
    onBlur?: (e: React.FocusEvent<HTMLInputElement>) => void;
    allowClear?: boolean;
}

export const DatePicker = (props: IDatePickerProps): JSX.Element => {
    const { name, onChange, onBlur, value, allowClear } = props;

    const { className, ...fieldProps } = props;

    const handleChange = useCallback(
        (val: Dayjs | null, valStr: string) => {
            onChange && onChange({ name, value: val?.toDate(), valueStr: valStr });
        },
        [name, onChange],
    );

    const dayJsValue = useMemo(() => (value ? dayjs(value) : undefined), [value]);
    return (
        <Field {...fieldProps} data-testid={`field-${name}`}>
            <div tw="relative">
                {props.readOnly && <Readonly />}
                <AntDatePickerWithDayjs
                    css={styles.input(!!props.readOnly)}
                    disabledDate={props.disableDate}
                    showTime={props.showTime}
                    tw="block"
                    value={dayJsValue}
                    name={name}
                    inputReadOnly={props.readOnly}
                    tabIndex={props.readOnly ? -1 : 0}
                    onChange={handleChange}
                    onBlur={onBlur}
                    allowClear={allowClear}
                />
            </div>
        </Field>
    );
};

const styles = {
    input: (readOnly: boolean): (SerializedStyles | boolean)[] => [
        readOnly &&
            css`
                border-color: ${THEME.READONLY_COLOR};
            `,
    ],
};
