import { DragOutlined } from '@ant-design/icons';
import { Icon } from '@common/general-ui/icon/icon.component';
import { Switch } from '@common/general-ui/switch/switch.component';
import { toBoolean } from '@common/utils/to-boolean.util';
import React, { useCallback } from 'react';
import 'twin.macro';
import { IColumnChooser } from './column-chooser.component';

interface IColumnChooserItemProps {
    column: IColumnChooser;
    onChange: (c: IColumnChooser) => void;
}

export const ColumnChooserItem = ({ column, onChange }: IColumnChooserItemProps): JSX.Element => {
    const handleChange = useCallback(
        (e: React.ChangeEvent<HTMLInputElement>) => {
            onChange({ ...column, isHidden: !toBoolean(e.target.value) });
        },
        [onChange, column],
    );
    return (
        <div tw="flex justify-between pt-1 px-1 items-center border-radius[4px]" data-testid={`column-item-${column.id}`}>
            <div tw="flex">
                <Icon icon={DragOutlined} tw="mt-0.5 mr-2" />
                <span>{column.title}</span>
            </div>
            <div>
                <Switch
                    name={column.id}
                    value={!column.isHidden}
                    onChange={handleChange}
                    readOnly={column.isAlwaysVisible}
                />
            </div>
        </div>
    );
};
