/* eslint-disable @typescript-eslint/no-explicit-any */
import { THEME } from '@common/constants/theme.constant';
import { ColumnChooser, IColumnChooser } from '@common/general-ui/column-chooser/column-chooser.component';
import { Loading } from '@common/general-ui/loading/loading.component';
import { Pagination } from '@common/general-ui/pagination/pagination.component';
import { useIsScrollEnd } from '@common/hooks/use-is-scroll-end.hook';
import { t } from '@common/hooks/use-t.hook';
import {
    ICollectionRequestOptions,
    IFetchMoreRequestOptions,
} from '@common/interfaces/collection-request-options.interface';
import { ICollection } from '@common/interfaces/collection.interface';
import { IIdObject } from '@common/interfaces/id-object.interface';
import { ITableUISettings } from '@common/interfaces/ui-settings.interface';
import { resizeEvent } from '@common/utils/event-emitter.util';
import { SerializedStyles } from '@emotion/react';
import { CheckboxProps, Table as AntdTable } from 'antd';
import { ColumnType } from 'antd/lib/table';
import { SorterResult, SortOrder, TablePaginationConfig, TableRowSelection } from 'antd/lib/table/interface';
import React, { ReactNode, useCallback, useEffect, useMemo, useRef, useState } from 'react';
import { css } from 'twin.macro';

export const createDefaultChooserColumns = (tableColumns: ITableColumn<any, any>[]): IColumnChooser[] => {
    return tableColumns.map((c): IColumnChooser => {
        return {
            id: c.id as string,
            title: c.title as string,
            isHidden: !c.isVisible,
            isAlwaysVisible: !!c.isAlwaysVisible,
        };
    });
};

export interface ITableColumn<T extends IIdObject, TKey> {
    title: ColumnType<T>['title'];
    render: (data: T) => React.ReactNode;
    id: TKey;
    size?: number | string;
    isVisible: boolean;
    isAlwaysVisible?: boolean;
    sorter?: ColumnType<T>['sorter'];
    sortOrder?: SortOrder;
    getCardAvatarUrl?: (data: T) => string | undefined;
}

interface IProps<T extends IIdObject, TKey> {
    name: string;
    className?: string;
    columns: ITableColumn<T, TKey>[];
    dataSource: ICollection<T> | undefined;
    selectedRows?: T[];
    onFetchMore?: (pagination: IFetchMoreRequestOptions) => void;
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    onTableOptionsChange?: (pagination: ICollectionRequestOptions<any>) => void;
    onSelectedRowsChange?: (selected: T[], isAllSelected: boolean) => void;
    isLoading: boolean;
    autoSize?: boolean;
    enableResize?: boolean;
    onChooserSettingsChange?: (settings: Partial<ITableUISettings>) => void | undefined;
    chooser?: { chooserColumns: IColumnChooser[]; defaultChooserColumns: IColumnChooser[] };
    showColumnChooser?: boolean;
}

const getCssGridFrames = (columns: ITableColumn<any, any>[], showSelection?: boolean): string => {
    const clonedColumns = [...columns];
    if (showSelection) {
        clonedColumns.unshift({
            id: 'id',
            size: 1,

            title: '',
            isVisible: true,
            render: () => null,
        });
    }
    const res = clonedColumns.map((c) => (c.size ? (typeof c.size === 'number' ? `${c.size}fr` : c.size) : '100fr'));
    return res.join(' ');
};

export function Table<T extends IIdObject, TKey>(props: IProps<T, TKey>): JSX.Element {
    const {
        columns,
        dataSource,
        autoSize,
        onFetchMore,
        chooser,
        onChooserSettingsChange,
        isLoading,
        onSelectedRowsChange,
        enableResize,
        showColumnChooser,
    } = props;

    const rootDivRef = useRef<HTMLDivElement | undefined>();
    const divTableContainerForCalcWidthRef = useRef<HTMLDivElement | undefined>();

    const [width, setWidth] = useState(rootDivRef?.current?.clientWidth || 0);
    useEffect(() => {
        const handleWindowResize = (): void => {
            if (divTableContainerForCalcWidthRef.current && rootDivRef.current) {
                divTableContainerForCalcWidthRef.current.style.position = 'absolute';
                rootDivRef.current.style.height = '9999999px';
            }
            setWidth(rootDivRef?.current?.clientWidth || 0);
            if (divTableContainerForCalcWidthRef.current && rootDivRef.current) {
                divTableContainerForCalcWidthRef.current.style.position = 'static';
                rootDivRef.current.style.height = 'auto';
            }
        };

        if (enableResize) {
            window.addEventListener('resize', handleWindowResize);
            resizeEvent.subscribe(handleWindowResize);
            handleWindowResize();
        }
        return (): void => {
            window.removeEventListener('resize', handleWindowResize);
            resizeEvent.unsubscribe(handleWindowResize);
        };
    }, [enableResize, setWidth]);

    const next_page = props.dataSource?.next_page;
    useIsScrollEnd({
        next_page,
        onScrollEnd: useCallback(() => {
            onFetchMore && onFetchMore({ next_page: props.dataSource?.next_page });
        }, [onFetchMore, props.dataSource?.next_page]),
    });

    const filteredColumns: ITableColumn<T, TKey>[] = useMemo(
        () =>
            (columns.filter((f) => f.isVisible) || []).map((c) => {
                return {
                    ...c,
                    dataIndex: c.id,
                    render: (_: any, data: any): ReactNode => c.render(data),
                } as unknown as ITableColumn<T, TKey>;
            }),
        [columns],
    );

    const cssGridFrames = useMemo(
        () => getCssGridFrames(filteredColumns, !!props.selectedRows),
        [filteredColumns, props.selectedRows],
    );

    const [selectedKeys, setSelectedKeys] = useState<IIdObject[]>([]);
    useEffect(() => {
        if (props.selectedRows) {
            setSelectedKeys(props.selectedRows);
        }
    }, [dataSource?.list.length, props.selectedRows]);

    const rowSelection = useMemo<TableRowSelection<any>>(() => {
        return {
            type: 'checkbox',
            selectedRowKeys: selectedKeys ? selectedKeys.map((f) => f.id) : [],
            onChange: (_: React.Key[], selectedRows: T[]): void => {
                setSelectedKeys(selectedRows);
                onSelectedRowsChange && onSelectedRowsChange(selectedRows, selectedRows.length === dataSource?.list.length);
            },
            getCheckboxProps: (record: IIdObject): Partial<CheckboxProps> => ({
                disabled: record.id === 'Disabled User', // Column configuration not to be checked
                name: record.id.toString(),
            }),
        };
    }, [dataSource?.list.length, onSelectedRowsChange, selectedKeys]);

    const handleTableChange = useCallback(
        (_pagination: TablePaginationConfig, _filter, _sorter: SorterResult<any> | SorterResult<any>[]) => {
            // if (dataSource) {
            //     const first = Array.isArray(sorter) ? sorter[0] : sorter;
            //     const data: ICollectionRequestOptions = {
            //         ...dataSource,
            //         limit: dataSource.request.limit || CONST.LIST_LIMIT,
            //         sortColumn: first.column?.dataIndex?.toString(),
            //         sortOrder: first.order,
            //     };
            //     onTableOptionsChange && onTableOptionsChange(data);
            // }
        },
        [],
    );

    const handleColumnChooserReset = useCallback(() => {
        onChooserSettingsChange &&
            onChooserSettingsChange({
                chooserColumns: undefined,
            });
    }, [onChooserSettingsChange]);

    const handleColumnsChange = useCallback(
        (columns: IColumnChooser[]) => {
            onChooserSettingsChange &&
                onChooserSettingsChange({
                    chooserColumns: columns,
                });
        },
        [onChooserSettingsChange],
    );

    const renderColumnChooser = (): JSX.Element | null => {
        if (!chooser) {
            return null;
        }
        return (
            <ColumnChooser
                tw="absolute right-0 top-0 z-50"
                asyncOnChange={true}
                columns={chooser.chooserColumns}
                onChange={handleColumnsChange}
                onResetClick={handleColumnChooserReset}
            />
        );
    };

    return (
        <Loading isLoading={isLoading}>
            <div tw="relative">
                {showColumnChooser ? renderColumnChooser() : undefined}
                <div
                    data-testid={props.name}
                    ref={rootDivRef as unknown as React.LegacyRef<HTMLDivElement>}
                    tw="relative overflow-x-auto"
                >
                    <div
                        style={enableResize ? { width: `${width || rootDivRef?.current?.clientWidth || 0}px` } : {}}
                        ref={divTableContainerForCalcWidthRef as unknown as React.LegacyRef<HTMLDivElement>}
                    >
                        <AntdTable
                            {...props}
                            locale={{ emptyText: t('No Data') }}
                            pagination={false}
                            columns={filteredColumns}
                            rowSelection={props.selectedRows ? rowSelection : undefined}
                            css={styles.table(!!autoSize, cssGridFrames, props.dataSource?.list)}
                            dataSource={props.dataSource?.list}
                            rowKey={'id'}
                            onChange={handleTableChange}
                            rowClassName={(_record, index): string =>
                                index % 2 === 0 ? 'table-row-light' : 'table-row-dark'
                            }
                        />
                        {dataSource && <Pagination tw="pt-4 text-right" dataSource={dataSource} onFetchMore={onFetchMore} />}
                        {isLoading && !props.dataSource?.list && <div tw="py-60"></div>}
                    </div>
                </div>
            </div>
        </Loading>
    );
}

