import { SerializedStyles } from '@emotion/react';
import React, { ReactNode } from 'react';
import { RegisterOptions } from 'react-hook-form';
import { css } from 'twin.macro';
import { FieldFooter, IFieldFooterProps } from './field-footer.component';
import { FieldLabel, IFieldLabelProps } from './field-label.component';

export interface IFieldProps<T = unknown, TName = string> extends Pick<RegisterOptions, 'validate'> {
    tooltip?: string;
    name: TName;
    label?: ReactNode;
    required?: boolean;
    value?: T;
    defaultValue?: T;
    placeholder?: string;
    helperText?: ReactNode;
    errorMessage?: ReactNode;
    readOnly?: boolean;
    size?: 'default' | 'small';
    onLabelClick?: () => void;
    getCustomErrorMessage?: (opts: RegisterOptions) => RegisterOptions;
}

interface IProps<TName> extends IFieldFooterProps, IFieldLabelProps {
    className?: string;
    name: IFieldProps<TName>['name'];
    children?: ReactNode;
    labelCss?: SerializedStyles;
}

// antd have similar component but this component use with antd-form (rc-field-form) https://github.com/react-component/field-form (this library has limitations)
export function Field<TName>({ children, onLabelClick, htmlFor, className, labelCss, ...rest }: IProps<TName>): JSX.Element {
    const { label } = rest;
    return (
        <div className={className} data-testid={`field-${rest.name}`} css={styles.field}>
            {label && <FieldLabel htmlFor={htmlFor} {...rest} onLabelClick={onLabelClick} css={labelCss} />}
            {children}
            <FieldFooter {...rest} />
        </div>
    );
}

const styles = {
    field: css`
        width: 100%;
    `,
};
