import { useField } from '@common/hooks/use-field.hook';
import React, { useCallback } from 'react';
import { IMultiSelectChangeHandler, IMultiSelectProps, MultiSelect } from './multi-select.component';

export function FormMultiSelect<TFormValues>(
    props: Omit<IMultiSelectProps<keyof TFormValues>, 'value' | 'defaultValue'>,
): JSX.Element {
    const { onChange, onBlur, getCustomErrorMessage } = props;
    const { setFieldValue, setFieldTouched, value, errorMessage } = useField<string[]>({
        ...props,
        getCustomErrorMessage,
    });

    const handleChange = useCallback<IMultiSelectChangeHandler>(
        (e) => {
            onChange && onChange(e);
            setFieldValue(e.value);
        },
        [setFieldValue, onChange],
    );

    const handleBlur = useCallback(
        (e: React.FocusEvent<HTMLInputElement>) => {
            const newEvent = {
                ...e,
                target: { ...e.target, value: value || [] },
            } as unknown as React.FocusEvent<HTMLInputElement>;
            onBlur && onBlur(newEvent);
            setFieldValue(value);
            setFieldTouched();
        },
        [onBlur, setFieldTouched, setFieldValue, value],
    );

    return (
        <MultiSelect
            errorMessage={errorMessage}
            {...props}
            onChange={handleChange}
            onBlur={handleBlur}
            name={props.name as string}
            value={value}
        />
    );
}
