import { THEME } from '@common/constants/theme.constant';
import React from 'react';
import { css } from 'twin.macro';
import { IFieldProps } from './field.component';

export interface IFieldLabelProps {
    label?: IFieldProps<unknown>['label'];
    required?: IFieldProps<unknown>['required'];
    htmlFor?: string;
    className?: string;
    onLabelClick?: () => void;
}

export const FieldLabel = ({ label, required, className, htmlFor, onLabelClick }: IFieldLabelProps): JSX.Element | null => {
    return (
        <label onClick={onLabelClick} htmlFor={htmlFor} css={styles.root} className={className}>
            {label}
            {required && <sup css={styles.required}>*</sup>}
        </label>
    );
};
const styles = {
    root: css`
        text-align: left;
        font-size: ${THEME.BASE_FONT_SIZE};
        font-weight: 700;
        color: black;
        display: inline-block;
        max-width: 100%;
        padding-bottom: 4px;
    `,
    required: css`
        color: #ff3304;
        font-size: 12px;
    `,
};
