import { IDataLayerObject, IDataLayerProduct, IEnhancedEcommerceObject } from '@common/interfaces/data-layer.interface';

declare global {
    interface Window {
        dataLayer?: IDataLayerObject[];
    }
}

const pushToDataLayer = (obj: IDataLayerObject): void => {
    window.dataLayer = window.dataLayer || [];
    window.dataLayer.push(obj);
};

const setUserData = (userId: string): void => {
    pushToDataLayer({
        event: 'customUser',
        userData: {
            userId,
        },
    });
};

const createPageEvent = (path: string): void => {
    const obj: IDataLayerObject = {
        event: 'customPage',
        pageData: {
            path,
        },
    };
    pushToDataLayer(obj);
};

const createCheckoutEnhancedEcommerceEvent = (step: string, product?: IDataLayerProduct[]): void => {
    const nullObj: IEnhancedEcommerceObject = {
        ecommerce: null,
    };

    const obj: IEnhancedEcommerceObject = {
        event: 'checkout',
        ecommerce: {
            checkout: {
                actionField: { step },
                ...(product && { products: product }),
            },
        },
    };
    pushToDataLayer(nullObj); // Clear the previous ecommerce object.
    pushToDataLayer(obj);
};

const createPurchaseEnhancedEcommerceEvent = (
    revenue: string,
    id: string,
    shipping: number,
    coupon: string,
    product?: IDataLayerProduct[],
): void => {
    const nullObj: IEnhancedEcommerceObject = {
        ecommerce: null,
    };

    const obj: IEnhancedEcommerceObject = {
        event: 'purchase',
        ecommerce: {
            purchase: {
                actionField: {
                    id,
                    revenue,
                    shipping,
                    coupon,
                },
                ...(product && { products: product }),
            },
        },
    };

    pushToDataLayer(nullObj);
    pushToDataLayer(obj);
};

const createGenericEvent = (
    category: string,
    action: string,
    label?: string,
    value?: number,
    nonInteraction?: boolean,
): void => {
    const obj: IDataLayerObject = {
        event: 'customEvent',
        eventData: {
            category,
            action,
            label,
            value,
            nonInteraction,
        },
    };
    pushToDataLayer(obj);
};

export const dataLayer = {
    createPageEvent,
    createGenericEvent,
    setUserData,
    createCheckoutEnhancedEcommerceEvent,
    createPurchaseEnhancedEcommerceEvent,
};
