import { IBEError } from '@common/interfaces/be-error.interface';
import { useCallback, useState } from 'react';

interface IReturn {
    start: (promises: Promise<unknown>[]) => void;
    loading: boolean;
    errors: IBEError[] | undefined;
    success: boolean;
}
export const usePromise = (): IReturn => {
    const [loading, setLoading] = useState(false);
    const [success, setSuccess] = useState(false);
    const [errors, setErrors] = useState<IBEError[] | undefined>();
    return {
        start: useCallback((promise: Promise<unknown>[]): void => {
            setErrors(undefined);
            setSuccess(false);
            setLoading(true);
            Promise.all(promise)
                .then(() => {
                    setSuccess(true);
                })
                .catch((error) => {
                    setErrors((prev): IBEError[] | undefined => {
                        if (prev) {
                            return [...prev, error];
                        }
                        return [error];
                    });
                })
                .finally(() => {
                    setLoading(false);
                });
        }, []),
        errors,
        success,
        loading,
    };
};
