import { IS_DEBUG } from '@common/constants/is-debug.constant';
import React, { ReactNode } from 'react';
import { css } from 'twin.macro';

interface IProps {
    children: ReactNode;
    className?: string;
}
export const DebugOnly = ({ children, className }: IProps): JSX.Element => {
    return IS_DEBUG ? (
        <div className={className} css={styles.root}>
            {children}
        </div>
    ) : (
        <></>
    );
};
const styles = {
    root: css`
        border: 1px solid red;
    `,
};
