import { ILink } from '@common/interfaces/link.interface';
import { comparePath } from './compare-path.util';

export const findLinksByPath = (pathName: string, links: ILink[]): ILink[] => {
    let res: ILink[] = [];
    links.forEach((link) => {
        if (link.subLinks) {
            const subRes = findLinksByPath(pathName, link.subLinks);
            if (subRes && subRes.length) {
                res.push(link);
            }
            res = [...res, ...subRes];
        }
        if (link.href && comparePath(link.href?.toString(), pathName)) {
            res.push(link);
        }
    });
    return res;
};
