import React from 'react';

export type ISwipeChangeArgs = 'left' | 'right';
interface IProps {
    children: JSX.Element | JSX.Element[];
    onSwipeX: (e: ISwipeChangeArgs) => void;
}

export function Swipe({ children, onSwipeX }: IProps): JSX.Element {
    const touchStart = React.useRef(0);
    const touchEnd = React.useRef(0);
    const swipeFired = React.useRef(false);

    function handleTouchStart(e: React.TouchEvent): void {
        touchStart.current = e.targetTouches[0].clientX;
    }

    function handleTouchMove(e: React.TouchEvent): void {
        touchEnd.current = e.targetTouches[0].clientX;
        if (touchStart.current - touchEnd.current > 150) {
            if (!swipeFired.current) {
                swipeFired.current = true;
                onSwipeX && onSwipeX('left');
            }
        }

        if (touchStart.current - touchEnd.current < -150) {
            if (!swipeFired.current) {
                swipeFired.current = true;
                onSwipeX && onSwipeX('right');
            }
        }
    }

    function handleTouchEnd(): void {
        swipeFired.current = false;
    }
    return (
        <div
            onTouchStart={handleTouchStart}
            onTouchMove={handleTouchMove}
            onTouchEnd={handleTouchEnd}
            // style={{ touchAction: 'pan-x' }}
        >
            {children}
        </div>
    );
}
