import { consoleService } from '@common/services/console.service';
import { useRef } from 'react';

export function useHookDependencyLogger(name: string, value: []): [] {
    const ref = useRef<[] | undefined>(undefined);

    if (ref.current) {
        const tmp: unknown[] = [];
        ref.current.forEach((val, index) => {
            const equal = val === value[index];
            if (!equal) {
                tmp[tmp.length] = {
                    index,
                    previousValue: val,
                    currentValue: value[index],
                    isEqual: equal,
                };
            }
        });
        if (tmp.length) {
            consoleService.log(`[${name}] -> hook dependency are different`, tmp);
        }
    } else {
        consoleService.log(`[${name}]-> hook dependency call first time (empty)`, value);
    }
    ref.current = value;
    return ref.current;
}
