import { DownOutlined } from '@ant-design/icons';
import { Icon } from '@common/general-ui/icon/icon.component';
import { Dropdown as AntdDropdown, DropDownProps } from 'antd';
import React, { LegacyRef, ReactNode, useEffect, useRef, useState } from 'react';

interface IProps {
    popup: JSX.Element;
    children: ReactNode;
    showArrowIcon?: boolean;
    placement?: DropDownProps['placement'];
    className?: string;
}

export const DropDown = ({ popup, children, showArrowIcon = true, placement, className }: IProps): JSX.Element => {
    const rootRef = useRef<HTMLDivElement | undefined>(undefined);
    const popupRef = useRef<HTMLDivElement | undefined>(undefined);
    const [visible, setIsVisible] = useState(false);

    useEffect(() => {
        if (visible) {
            window.addEventListener('click', handleDropdownClose);
        }
        return (): void => window.removeEventListener('click', handleDropdownClose);
    }, [visible]);

    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    const handleDropdownClose = (e: any): void => {
        const path = e.path || e.composedPath();
        for (const item of path) {
            if (rootRef.current === item) {
                return;
            }
            if (popupRef.current === item) {
                return;
            }
        }

        setIsVisible(false);
    };
    return (
        <div ref={rootRef as unknown as LegacyRef<HTMLDivElement>} className={className}>
            <AntdDropdown
                visible={visible}
                overlay={
                    <div tw="text-base" ref={popupRef as unknown as LegacyRef<HTMLDivElement>}>
                        {popup}
                    </div>
                }
                trigger={['click']}
                placement={placement}
            >
                <div tw="cursor-pointer" onClick={(): void => setIsVisible(!visible)}>
                    {children} {showArrowIcon && <Icon icon={DownOutlined} />}
                </div>
            </AntdDropdown>
        </div>
    );
};
