import { EventEmitter } from '@common/utils/event-emitter.util';
import { useCallback, useEffect, useRef } from 'react';

export interface IMessage<T> {
    channel: string;
    data: T;
}
interface IProps {
    iframe: HTMLIFrameElement | undefined;
}
interface IReturn<T> {
    postToParent: (name: string, data?: T) => void;
    postToIframe: (name: string, data?: T) => void;
    subscribe: (name: string, callback: (data: T) => void) => () => void;
}
export function useMessage<T>({ iframe }: IProps): IReturn<T> {
    const events = useRef<Record<string, EventEmitter>>({});
    useEffect(() => {
        window.onmessage = (e): void => {
            if (e.data) {
                try {
                    const data: IMessage<T> = JSON.parse(e.data);
                    if (data.channel) {
                        events.current[data.channel].emit(data.data);
                    }
                    // eslint-disable-next-line no-empty
                } catch {}
            }
        };
    }, [iframe]);

    return {
        postToParent: useCallback((channel: string, data) => {
            if (window.top) {
                window.top?.postMessage(
                    JSON.stringify({
                        channel,
                        data,
                    } as IMessage<T>),
                    '*',
                );
            }
        }, []),

        postToIframe: useCallback(
            (channel: string, data) => {
                iframe?.contentWindow?.postMessage(
                    JSON.stringify({
                        channel,
                        data,
                    } as IMessage<T>),
                    '*',
                );
            },
            [iframe?.contentWindow],
        ),

        subscribe: useCallback(
            (channel: string, callback: (arg: T) => void) => {
                if (!events.current[channel]) {
                    events.current[channel] = new EventEmitter();
                }
                events.current[channel].subscribe(callback);
                return (): void => events.current[channel].unsubscribe(callback);
            },
            [events],
        ),
    };
}
