import what3words, {
    ApiVersion,
    AutosuggestOptions,
    AutosuggestResponse,
    axiosTransport,
    What3wordsService,
} from '@what3words/api';
import { useMutation, UseMutationResult } from 'react-query';

const apiKey: string = process.env.REACT_APP_WHAT_3_WORDS_KEY as string;
const config: {
    host: string;
    apiVersion: ApiVersion;
} = {
    host: 'https://api.what3words.com',
    apiVersion: ApiVersion.Version3,
};
export const w3wService: What3wordsService = what3words(apiKey, config, { transport: axiosTransport() });

const NAME = 'what3words';
export const useWhat3Words = (): UseMutationResult<AutosuggestResponse, unknown, AutosuggestOptions> => {
    return useMutation<AutosuggestResponse, unknown, AutosuggestOptions>(
        (props) => {
            return w3wService.autosuggest(props);
        },
        {
            mutationKey: `${NAME}-what3words`,
        },
    );
};
