import { ICollectionRequestOptions } from '@common/interfaces/collection-request-options.interface';
import { IIdObject } from '@common/interfaces/id-object.interface';
import { IInfiniteCollection } from '@common/interfaces/infinite-collection.interface';
import { UID } from '@common/interfaces/uid.interface';
import {
    addToCollection,
    createCollection,
    deleteFromCollection,
    getItem,
    updateCollection,
} from './create-collection.util';

export function createInfiniteCollection<T>(
    data: T[] | undefined,
    next_page: number | undefined,
    request: Omit<ICollectionRequestOptions, 'limit'> & { limit?: number },
): IInfiniteCollection<T> {
    return {
        pageParams: [0],
        pages: [createCollection(data, next_page, request)],
    };
}

export function deleteFromInfiniteCollection<T extends IIdObject>(
    data: IInfiniteCollection<T> | undefined,
    id: UID,
): IInfiniteCollection<T> {
    if (!data) {
        return createInfiniteCollection([], 0, {});
    }
    return {
        ...data,
        pages: data.pages.map((page) => {
            if (getItem(page, id)) {
                return deleteFromCollection(page, id);
            }
            return page;
        }),
    };
}

// TODO implement
export function addToInfiniteCollection<T extends IIdObject>(
    data: IInfiniteCollection<T> | undefined,
    item: T,
): IInfiniteCollection<T> {
    if (!data) {
        return createInfiniteCollection([item], 0, {});
    }
    const [first, ...rest] = data.pages;
    return {
        ...data,
        pages: [addToCollection(first, item), ...rest],
    };
}

// TODO implement
export function updateInfiniteCollection<T extends IIdObject>(
    data: IInfiniteCollection<T> | undefined,
    id: UID,
    newItem: T,
): IInfiniteCollection<T> {
    if (!data) {
        return createInfiniteCollection([newItem], 0, {});
    }
    return {
        ...data,
        pages: data.pages.map((page) => {
            if (getItem(page, id)) {
                return updateCollection(page, id, newItem);
            }
            return page;
        }),
    };
}
