import { SerializedStyles } from '@emotion/react';
import { Drawer as AntDrawer, DrawerProps } from 'antd';
import React, { ReactNode } from 'react';
import { css } from 'twin.macro';

interface IDrawerProps {
    title?: string;
    placement?: DrawerProps['placement'];
    closable?: DrawerProps['closable'];
    onClose?: DrawerProps['onClose'];
    visible?: DrawerProps['visible'];
    children?: ReactNode;
    width?: string;
    showCloseIcon?: boolean;
    bodyCss?: SerializedStyles;
}

export const Drawer = (props: IDrawerProps): JSX.Element => {
    const { title, onClose, visible, placement, closable, children } = props;
    return (
        <AntDrawer
            css={styles.root(props)}
            title={title}
            placement={placement}
            closable={closable}
            onClose={onClose}
            visible={visible}
        >
            {children}
        </AntDrawer>
    );
};
const styles = {
    root: ({ width, showCloseIcon = true, bodyCss }: IDrawerProps): (SerializedStyles | boolean)[] => [
        css`
            .ant-drawer-body {
                padding: 0px;
            }
            .ant-drawer-close {
                background-color: white;
            }
        `,
        bodyCss !== undefined &&
            css`
                && .ant-drawer-body {
                    ${bodyCss}
                }
            `,
        width !== undefined &&
            css`
                .ant-drawer-content-wrapper {
                    width: ${width} !important;
                }
            `,
        !showCloseIcon &&
            css`
                && .ant-drawer-close {
                    display: none;
                }
            `,
    ],
};
