import { useCallback, useEffect, useRef } from 'react';

export const useIsMounted = (): { isMounted: () => boolean } => {
    const ref = useRef(false);

    useEffect(() => {
        ref.current = true;
        return (): void => {
            ref.current = false;
        };
    }, []);
    return { isMounted: useCallback((): boolean => ref.current, []) };
};
