import { CheckCircleOutlined, CloseCircleOutlined, ExclamationCircleOutlined, InfoCircleOutlined } from '@ant-design/icons';
import { MQ } from '@common/constants/mq.constant';
import { Icon } from '@common/general-ui/icon/icon.component';
import { SerializedStyles } from '@emotion/react';
import { Tag as AntdTag, TagProps } from 'antd';
import { PresetStatusColorType } from 'antd/lib/_util/colors';
import React, { ReactNode } from 'react';
import { css } from 'twin.macro';

export type TStatuses = 'empty' | 'default' | 'error' | 'warning' | 'info' | 'success';

interface IProps extends Omit<TagProps, 'color'> {
    status: TStatuses | undefined;
    children: ReactNode;
    className?: string;
}

const ICONS_MAP: Record<TStatuses, ReactNode> = {
    empty: <></>,
    success: <Icon icon={CheckCircleOutlined} />,
    info: <Icon icon={InfoCircleOutlined} />,
    error: <Icon icon={CloseCircleOutlined} />,
    default: <Icon icon={InfoCircleOutlined} />,
    warning: <Icon icon={ExclamationCircleOutlined} />,
};
// const TITLE_MAP: Record<TStatuses, ReactNode> = {
//     empty: '',
//     success: t('Success'),
//     info: t('Info'),
//     error: t('Error'),
//     default: t('Default'),
//     warning: t('Warning'),
// };
const COLOR_MAP: Record<TStatuses, TagProps['color']> = {
    empty: 'black',
    success: 'green',
    info: 'blue',
    error: 'red',
    default: 'black',
    warning: '#cc8500',
};

// const TEXT_COLOR: Record<TStatuses, TagProps['color']> = {
//     empty: 'black',
//     success: 'white',
//     info: 'white',
//     error: 'white',
//     default: 'black',
//     warning: 'white',
// };

const LIGHT_COLOR_MAP: Record<TStatuses, TagProps['color']> = {
    empty: 'default',
    success: '#c4dcc8',
    info: '#bebcdc',
    error: '#ff8080',
    default: '#d9d9d9',
    warning: '#f8dba0',
};

export const StatusTag = ({ status = 'default', ...rest }: IProps): JSX.Element => {
    const icon =
        status && ICONS_MAP[status as PresetStatusColorType] ? ICONS_MAP[status as PresetStatusColorType] : undefined;
    return (
        <AntdTag
            // eslint-disable-next-line @typescript-eslint/no-explicit-any
            css={styles.root(status, LIGHT_COLOR_MAP[status] as string) as any}
            icon={icon}
            {...rest}
            color={COLOR_MAP[status]}
            tw="whitespace-normal "
            className={rest.className}
        >
            <div tw="whitespace-normal"> {rest.children}</div>
        </AntdTag>
    );
};

const styles = {
    root: (status: TStatuses, color: string): (SerializedStyles | boolean)[] => [
        css`
            margin: 0;
            display: flex;
            border-width: 1px;
            font-size: 12px;
            padding: 0.3rem;
            letter-spacing: 1px;
            font-weight: 200;
            background-color: ${color}!important;
            color: black;
            width: fit-content;
            height: min-content;
            border: 0px;
            border-radius: 4px;
            svg {
                font-size: 18px;
            }

            ${MQ.sm} {
                padding: 0.5rem 0.75rem;
            }

            ${MQ.md} {
                font-size: 14px;
                padding: 0.5rem 1rem;
            }
        `,
        status === 'default' && css``,
        status === 'error' && css``,
        status === 'info' && css``,
        status === 'success' && css``,
        status === 'warning' && css``,
    ],
};
