import { IDictionary } from '@common/interfaces/dictionary.interface';
import { queryToString } from './query-to-string.util';

export function formatUrl<T = IDictionary>(urlStr: string, query?: T): string {
    if (query && Object.keys(query).length) {
        return `${urlStr}?${queryToString(query)}`;
    }
    return urlStr;
}
