import { MQ } from '@common/constants/mq.constant';
import { THEME } from '@common/constants/theme.constant';
import { ISwipeChangeArgs, Swipe } from '@common/general-ui/swipe/swipe.component';
import { ILinkItem } from '@common/utils/make-links.util';
import { TabPaneProps, Tabs as AntdTabs, TabsProps } from 'antd';
import React, { ReactNode, useCallback, useEffect, useState } from 'react';
import { css } from 'twin.macro';

const { TabPane: AntdTabPane } = AntdTabs;

export interface ITabItem<TName = string> {
    name: TName;
    title: ReactNode;
    href?: ILinkItem;
    isVisible: boolean;
}

interface IProps<T extends ITabItem = ITabItem> {
    defaultActiveKey?: string;
    tabs: T[];
    activeKey?: TabsProps['activeKey'];
    onChange?: (tabItem: T) => void;
    children?: JSX.Element | JSX.Element[] | null;
    name: string;
}

export function Tabs<T extends ITabItem>({ name, children, tabs, onChange, ...rest }: IProps<T>): JSX.Element {
    const [activeTab, setActiveTab] = useState(rest.activeKey);

    useEffect(() => {
        if (rest.defaultActiveKey !== undefined) {
            setActiveTab(rest.defaultActiveKey);
        }
        if (rest.activeKey !== undefined) {
            setActiveTab(rest.activeKey);
        }
    }, [rest.activeKey, rest.defaultActiveKey]);

    const handleChange = useCallback(
        (key): void => {
            onChange && onChange(tabs.find((f) => f.name === key) as T);
        },
        [onChange, tabs],
    );

    const handleSwipe = useCallback(
        (e: ISwipeChangeArgs): void => {
            if (e === 'left') {
                // do your stuff here for left swipe
                const activeIndex = tabs.findIndex((t) => t.name === activeTab);
                if (activeIndex + 1 < tabs.length) {
                    setActiveTab(tabs[activeIndex + 1].name);
                    onChange && onChange(tabs[activeIndex + 1]);
                }
            }

            if (e === 'right') {
                // do your stuff here for right swipe
                const activeIndex = tabs.findIndex((t) => t.name === activeTab);
                if (activeIndex - 1 >= 0) {
                    setActiveTab(tabs[activeIndex - 1].name);
                    onChange && onChange(tabs[activeIndex - 1]);
                }
            }
        },
        [activeTab, onChange, tabs],
    );
    return (
        <div css={styles.root}>
            <AntdTabs
                data-testid={`tabs-${name}`}
                css={styles.tabs}
                {...rest}
                onChange={handleChange}
                animated={false}
                activeKey={activeTab}
            >
                {tabs.map((tab) => {
                    return <TabPane tab={<span data-testid={`tab-${tab.name}`}>{tab.title}</span>} key={tab.name}></TabPane>;
                })}
            </AntdTabs>
            <Swipe onSwipeX={handleSwipe}>
                <>{children}</>
            </Swipe>
        </div>
    );
}
//ant-tabs-nav-list
const styles = {
    root: css`
	.ant-tabs-nav-list{
		transition: transform!important
	}
		.ant-tags-nav-list {
			position:static!important;
			translate(-272px, 0px)
		}
        width: calc(100vw - 36px);
        ${MQ.lg} {
            width: auto;
        }
    `,
    tabs: css`
        font-size: ${THEME.BASE_FONT_SIZE};
        && .ant-tabs-tab-btn {
            text-transform: uppercase;
            font-size: ${THEME.BASE_FONT_SIZE};
        }

        && .ant-tabs-nav-list {
        }

        && .ant-tabs-tab-active {
        }

        && .ant-tabs-nav-wrap-ping-right::after {
            opacity: 0 !important;
        }
        && .ant-tabs-nav-wrap-ping-left::before {
            opacity: 0 !important;
        }
    `,
};

const TabPane = ({ ...rest }: TabPaneProps): JSX.Element => {
    return <AntdTabPane {...rest}>{rest.children}</AntdTabPane>;
};
