import { consoleService } from '@common/services/console.service';
import { useMemo, useState } from 'react';

interface IState {
    type: 'boolean' | 'string';
    value: string;
}

export function useSessionState<T>(key: string, defaultValue: T): [T, (arg: T) => void] {
    const value = useMemo(() => {
        const val = sessionStorage.getItem(key);
        if (val) {
            try {
                const json: IState = JSON.parse(val as string) as IState;
                return json.value;
            } catch (ex) {
                consoleService.error(ex);
            }
        }
        return undefined;
    }, [key]);

    const [state, setState] = useState<T>(value !== undefined ? (value as unknown as T) : defaultValue);
    return [
        state,
        (newState): void => {
            sessionStorage.setItem(
                key,
                JSON.stringify({
                    type: typeof newState,
                    value: newState,
                } as unknown as IState),
            );
            setState(newState);
        },
    ];
}
