import { THEME } from '@common/constants/theme.constant';
import { Card } from '@common/general-ui/card/card.component';
import { BGImage } from '@common/general-ui/image/image.component';
import { ITableColumn } from '@common/general-ui/table/table.component';
import { t } from '@common/hooks/use-t.hook';
import { IIdObject } from '@common/interfaces/id-object.interface';
import { SerializedStyles } from '@emotion/react';
import React, { ReactNode, useMemo } from 'react';
import { css } from 'twin.macro';

interface IProps<T extends IIdObject, TKey> {
    header?: ITableColumn<T, TKey>;
    renderHeader?: () => JSX.Element;
    columns: ITableColumn<T, TKey>[];
    rowData: T;
    className?: string;
    isFullWidth?: boolean;
    noHeader?: boolean;
}

export function getTableColumnValue<T extends IIdObject, TKey>(rowData: T, column: ITableColumn<T, TKey>): ReactNode {
    return column.render(rowData);
}

export function getTableColumnValueByName<T extends IIdObject, TKey extends string>(
    columns: ITableColumn<T, TKey>[],
    id: TKey,
    rowData: T,
): ReactNode {
    const column = columns.find((c) => c.id === id);
    if (column) {
        return column.render(rowData);
    } else {
        return <>{t('Not found')}</>;
    }
}

export function MobileTableRow<T extends IIdObject, TKey>({
    rowData,
    columns,
    header,
    className,
    isFullWidth,
    noHeader,
}: IProps<T, TKey>): JSX.Element {
    const rows = useMemo(() => {
        return columns.length
            ? columns
                  .map((column, index) => {
                      const value = getTableColumnValue<T, TKey>(rowData, column);
                      return value !== undefined ? (
                          <div key={column.id as unknown as string} css={styles.item(index % 2 === 1)}>
                              <div
                                  tw="grid grid-cols-2 py-2 grid-template-columns[  1fr minmax(0, 2fr)] grid-row-gap[16px] grid-column-gap[16px]"
                                  css={isFullWidth && styles.fullWidth}
                              >
                                  <span className="key" tw="font-bold">
                                      {column.title}&nbsp;
                                  </span>
                                  <span css={styles.value}>{value}</span>
                              </div>
                          </div>
                      ) : null;
                  })
                  .filter((f) => f)
            : null;
    }, [columns, isFullWidth, rowData]);

    const avatarUrl = useMemo(() => {
        const res = header?.getCardAvatarUrl ? header.getCardAvatarUrl(rowData) : '';
        return res;
    }, [header, rowData]);

    const headerContent = useMemo(() => {
        return header ? getTableColumnValue(rowData, header) : undefined;
    }, [header, rowData]);
    return (
        <Card
            className={className}
            avatarUrl={avatarUrl}
            header={headerContent}
            headerCss={styles.headerCss}
            contentCss={styles.contentCss}
            noHeader={noHeader}
        >
            {avatarUrl ? (
                <div tw="grid md:grid-cols-2">
                    <BGImage css={{ padding: '35%' }} tw="h-auto w-full " src={avatarUrl} />
                    <div>{rows}</div>
                </div>
            ) : (
                rows
            )}
        </Card>
    );
}

const styles = {
    headerCss: css`
        background-color: ${THEME.GREEN};
        border: 1px solid ${THEME.GRAY};
        font-weight: bold;
        color: white;
        * {
            color: white !important;
        }
    `,
    contentCss: css`
        padding: 0px;
    `,
    fullWidth: css`
        grid-template-columns: 1fr minmax(0, 1fr);
    `,
    item: (odd: boolean): (SerializedStyles | boolean)[] => [
        css`
            padding: 8px 16px;
        `,
        odd &&
            css`
                background-color: ${THEME.LIGHT_GRAY};
            `,
    ],
    value: css`
        overflow: hidden;
        text-overflow: ellipsis;
        color: inherit;
        font-weight: 200;
    `,
};
