import { IS_DEBUG } from '@common/constants/is-debug.constant';
import { JsonPreview } from '@common/general-ui/json-preview/json-preview.component';
import { ITableColumn } from '@common/general-ui/table/table.component';
import { t } from '@common/hooks/use-t.hook';
import { IIdObject } from '@common/interfaces/id-object.interface';
import React from 'react';

export function createJSONTableColumn<T extends IIdObject, TKey>({ id }: { id: TKey }): ITableColumn<T, TKey> {
    return {
        isVisible: IS_DEBUG,

        title: t('JSON'),
        id,
        render: (data): JSX.Element => <JsonPreview json={data} />,
    };
}
