import { StatusTag } from '@common/general-ui/tag/status-tag.component';
import { t } from '@common/hooks/use-t.hook';
import { consoleService } from '@common/services/console.service';
import { PaymentRequestButtonElement, useStripe } from '@stripe/react-stripe-js';
import { SetupIntentResult, StripePaymentRequestButtonElementOptions } from '@stripe/stripe-js';
import React, { useEffect, useMemo, useState } from 'react';

interface IProps {
    onTokenInit?: (token: SetupIntentResult) => void;
    clientSecret: string;
}
export const PaymentButton = ({ onTokenInit, clientSecret }: IProps): JSX.Element => {
    const stripe = useStripe();
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    const [paymentRequest, setPaymentRequest] = useState<any>(null);
    const [message, setMessage] = useState('');

    useEffect(() => {
        if (stripe) {
            const pr = stripe.paymentRequest({
                country: 'GB',
                currency: 'gbp',
                total: {
                    label: 'Subscribe',
                    amount: 0,
                },
                requestPayerName: true,
                requestPayerEmail: true,
                requestPayerPhone: true,
                requestShipping: false,
            });

            // Full example https://github.com/stripe-samples/accept-a-payment/blob/main/custom-payment-flow/client/react-cra/src/GooglePay.js
            // Check the availability of the Payment Request API.
            pr.canMakePayment()
                .then((result) => {
                    if (result) {
                        setPaymentRequest(pr);
                    } else {
                        setMessage(
                            t(
                                'Google Pay / Apple Pay not detected, please check your device settings or choose Credit / Debit card',
                            ),
                        );
                    }
                })
                .catch((e) => consoleService.log('error =', e));
        }
    }, [onTokenInit, stripe]);

    useEffect(() => {
        if (paymentRequest && stripe) {
            // eslint-disable-next-line @typescript-eslint/no-explicit-any
            paymentRequest?.on('paymentmethod', async (ev: any) => {
                stripe
                    ?.confirmCardSetup(clientSecret, {
                        payment_method: ev.paymentMethod.id,
                    })
                    .then((setupIndent) => {
                        if (!setupIndent.error) {
                            onTokenInit && onTokenInit(setupIndent);
                            setMessage(t('Card successfully initialized'));
                            consoleService.log('confirmCardSetup success', setupIndent);
                            ev.complete('success');
                        } else {
                            consoleService.log('confirmCardSetup error', setupIndent);
                            ev.complete('fail');
                        }
                    })
                    .catch((e) => {
                        consoleService.log('confirmCardSetup error', e);
                        ev.complete('fail');
                    });
            });
        }
    }, [clientSecret, onTokenInit, paymentRequest, stripe]);

    const handleReady = (e: unknown): void => {
        consoleService.log('handleReady', e);
    };
    const options = useMemo(
        () =>
            ({
                paymentRequest,
                style: {
                    paymentRequestButton: {
                        // theme: 'dark',
                        height: '42px',
                        type: 'default',
                    },
                },
            } as unknown as StripePaymentRequestButtonElementOptions),
        [paymentRequest],
    );

    if (options && paymentRequest) {
        return (
            <>
                <PaymentRequestButtonElement options={options} onReady={handleReady} />

                {message && (
                    <StatusTag tw="mt-1" status="success">
                        {message}
                    </StatusTag>
                )}
            </>
        );
    } else {
        if (message) {
            return <StatusTag status="info">{message}</StatusTag>;
        }
    }

    return <></>;
};
