import { CalendarOutlined, CreditCardOutlined, LockFilled } from '@ant-design/icons';
import { EPaymentMethod } from '@common/enums/payment-method.enum';
import { FieldLabel } from '@common/general-ui/field/field-label.component';
import { RadioGroup } from '@common/general-ui/radio-group/radio-group.component';
import { IRadioChangeHandler, IRadioOption, IRadioProps, Radio } from '@common/general-ui/radio-group/radio.component';
import { t } from '@common/hooks/use-t.hook';
import { CardCvcElement, CardExpiryElement, CardNumberElement, useElements, useStripe } from '@stripe/react-stripe-js';
import {
    SetupIntentResult,
    Stripe,
    StripeCardCvcElementChangeEvent,
    StripeCardElementOptions,
    StripeCardExpiryElementChangeEvent,
    StripeCardNumberElement,
    StripeCardNumberElementChangeEvent,
} from '@stripe/stripe-js';
import React, { useCallback, useEffect, useState } from 'react';
import { css } from 'twin.macro';
import { PaymentButton } from './payment-button.component';

export interface IStripeCard {
    cardNumberElement: StripeCardNumberElement | null | undefined;
    isValid: boolean;
}

interface ICardValidation {
    cardNumber: boolean;
    cardExpiry: boolean;
    cardCvc: boolean;
}

interface IProps {
    onCardChange?: (e: IStripeCard) => void;
    onBlur?: () => void;
    onStripeInit?: (stripe: Stripe) => void;
    onPaymentButtonChange?: (token: SetupIntentResult) => void;
    onTabChange?: (tab: EPaymentMethod) => void;
    paymentButtonClientSecret?: string;
    paymentMethodOptions: IRadioOption[];
    defaultOptionName?: EPaymentMethod;
}

export const StripeInternal = ({
    onCardChange,
    onBlur,
    onPaymentButtonChange,
    onStripeInit,
    onTabChange,
    paymentMethodOptions,
    paymentButtonClientSecret,
    defaultOptionName,
}: IProps): JSX.Element => {
    const stripe = useStripe();
    const [radioValue, setRadioValue] = useState(defaultOptionName || EPaymentMethod.card);
    const [cardValidation, setCardValidation] = useState<ICardValidation>({
        cardNumber: false,
        cardCvc: false,
        cardExpiry: false,
    });
    const elements = useElements();

    useEffect(() => {
        if (stripe) {
            onStripeInit && onStripeInit(stripe);
        }
    }, [onStripeInit, stripe]);

    const cardNumberElement = elements?.getElement(CardNumberElement);

    const handleChange = useCallback(
        (
            event: StripeCardNumberElementChangeEvent | StripeCardExpiryElementChangeEvent | StripeCardCvcElementChangeEvent,
        ) => {
            const newCardValidation = {
                ...cardValidation,
                [event.elementType]: event.complete,
            };
            onCardChange &&
                onCardChange({
                    cardNumberElement,
                    isValid: newCardValidation.cardNumber && newCardValidation.cardExpiry && newCardValidation.cardCvc,
                });
            setCardValidation(newCardValidation);
        },
        [cardNumberElement, cardValidation, onCardChange],
    );

    const handleBlur = useCallback(() => {
        onBlur && onBlur();
    }, [onBlur]);

    const renderPaymentOption = useCallback(
        (props: IRadioProps, _value: string | undefined) => {
            return (
                <div>
                    {paymentMethodOptions.length > 1 ? (
                        <Radio {...props} option={{ ...props.option }} name={props.name} />
                    ) : (
                        <FieldLabel label={paymentMethodOptions[0]?.label} />
                    )}
                    <div tw="py-2">
                        {radioValue === props.option.value && radioValue === EPaymentMethod.card && (
                            <div
                                css={styles.fieldContainer}
                                tw="grid grid-cols-3 grid-column-gap[8px] grid-row-gap[8px] justify-between"
                            >
                                <div tw="col-span-6 md:col-span-2 relative">
                                    <CreditCardOutlined tw="text-base flex items-center h-full ml-3 opacity-50 absolute" />
                                    <CardNumberElement
                                        options={stripeUiOptions}
                                        onChange={handleChange}
                                        onBlur={handleBlur}
                                    />
                                </div>
                                <div tw="col-span-6 md:col-span-1 grid grid-flow-col grid-column-gap[8px] grid-row-gap[8px]">
                                    <div tw="relative">
                                        <CalendarOutlined tw="text-base flex items-center h-full ml-3 opacity-50 absolute" />
                                        <CardExpiryElement
                                            options={stripeUiOptions}
                                            onChange={handleChange}
                                            onBlur={handleBlur}
                                        />
                                    </div>
                                    <div tw="relative">
                                        <LockFilled tw="text-base flex items-center h-full ml-3 opacity-50 absolute" />
                                        <CardCvcElement
                                            options={stripeUiOptions}
                                            onChange={handleChange}
                                            onBlur={handleBlur}
                                        />
                                    </div>
                                </div>
                            </div>
                        )}
                        {radioValue === props.option.value &&
                            radioValue === EPaymentMethod.button &&
                            paymentButtonClientSecret && (
                                <PaymentButton
                                    onTokenInit={onPaymentButtonChange}
                                    clientSecret={paymentButtonClientSecret}
                                />
                            )}
                    </div>
                </div>
            );
        },
        [paymentMethodOptions, radioValue, handleChange, handleBlur, paymentButtonClientSecret, onPaymentButtonChange],
    );

    const handleRadioChange = useCallback<IRadioChangeHandler>(
        (e) => {
            const val = e.target.name as unknown as EPaymentMethod;
            setRadioValue(val);
            onTabChange && onTabChange(val);
        },
        [onTabChange],
    );

    return (
        <div css={styles.root}>
            <RadioGroup
                label={paymentMethodOptions.length > 1 ? t('Select Payment Method') : undefined}
                name="paymentMethod"
                defaultValue={defaultOptionName}
                onChange={handleRadioChange}
                value={radioValue}
                options={paymentMethodOptions}
                renderOption={renderPaymentOption}
            />
        </div>
    );
};

const styles = {
    root: css`
        padding-top: 4px;
    `,
    fieldContainer: css`
        .StripeElement {
            border: 1px solid gray;
            border-radius: 4px;
            padding: 6px 0 6px 36px;
        }
    `,
};

const stripeUiOptions: StripeCardElementOptions = {
    style: {
        base: {
            fontSize: '16px',
            color: '#424770',
            letterSpacing: '0.025em',
            fontFamily: 'Interstate, Overpass',

            '::placeholder': {
                color: '#aab7c4',
            },
        },
        invalid: {
            color: '#9e2146',
        },
    },
};
