import { notification } from 'antd';
import { ReactNode } from 'react';

interface INotifyProps {
    title: ReactNode;
    description?: ReactNode;
}

const duration = 10;
let visibleNotifies: string[] = [];
export const notify = {
    success: (props: INotifyProps): void => {
        const id = `${props.title}-${props.description}`;
        if (!visibleNotifies.includes(id)) {
            visibleNotifies.push(id);

            notification.success({
                message: props.title,
                description: props.description,
                duration,
                onClose: () => {
                    visibleNotifies = visibleNotifies.filter((f) => f !== id);
                },
            });
        }
    },
    info: (props: INotifyProps): void => {
        const id = `${props.title}-${props.description}`;
        if (!visibleNotifies.includes(id)) {
            visibleNotifies.push(id);

            notification.info({
                message: props.title,
                description: props.description,
                duration,
                onClose: () => {
                    visibleNotifies = visibleNotifies.filter((f) => f !== id);
                },
            });
        }
    },
    error: (props: INotifyProps): void => {
        const id = `${props.title}-${props.description}`;
        if (!visibleNotifies.includes(id)) {
            visibleNotifies.push(id);

            notification.error({
                message: props.title,
                description: props.description,
                duration,
                onClose: () => {
                    visibleNotifies = visibleNotifies.filter((f) => f !== id);
                },
            });
        }
    },
};
