import { consoleService } from '@common/services/console.service';
import { useRef } from 'react';

const deepCompareEquals = (a: unknown, b: unknown): boolean => {
    return JSON.stringify(a) === JSON.stringify(b);
};

export function useDeepCompareMemorized<T>({ title, enabled, value }: { title: string; value: T; enabled: boolean }): T {
    const ref = useRef<T>(value);

    if (enabled && !deepCompareEquals(value, ref.current)) {
        consoleService.log(`deepCompareEquals for '${title}' are different`, value, ref.current);

        ref.current = value;
    }

    return ref.current;
}
