import { SearchOutlined, UndoOutlined, UnorderedListOutlined } from '@ant-design/icons';
import { THEME } from '@common/constants/theme.constant';
import { DropDownPopup } from '@common/general-ui/drop-down/drop-down-popup.component';
import { DropDown } from '@common/general-ui/drop-down/drop-down.component';
import { Icon } from '@common/general-ui/icon/icon.component';
import { Input } from '@common/general-ui/input/input.component';
import { Link } from '@common/general-ui/link/link.component';
import { t } from '@common/hooks/use-t.hook';
import { useTimeout } from '@common/hooks/use-timeout.hook';
import { UID } from '@common/interfaces/uid.interface';
import { arrayUtils } from '@common/utils/array-utils.util';
import { Button } from 'antd';
import React, { useCallback, useMemo, useState } from 'react';
import { css } from 'twin.macro';
import { ColumnChooserItem } from './column-chooser-item.component';
import { ColumnChooserList } from './column-chooser-list.component';

export interface IColumnChooser {
    id: UID;
    title: string;
    isAlwaysVisible: boolean;
    isHidden: boolean;
}

interface IProps {
    columns: IColumnChooser[];
    onChange: (newColumns: IColumnChooser[]) => void;
    onResetClick?: () => void;
    asyncOnChange?: boolean;
    className?: string;
}

export const ColumnChooser = ({
    columns,
    onChange: onChange,
    onResetClick,
    asyncOnChange,
    className,
}: IProps): JSX.Element => {
    const { start } = useTimeout<IColumnChooser[]>(onChange);
    const [search, setSearch] = useState<string>('');

    const filteredColumns = useMemo(() => {
        return columns.filter((f) => f.title.toLowerCase().includes(search.toLowerCase()));
    }, [columns, search]);

    const handleChange = useCallback(
        (c: IColumnChooser[]) => {
            if (asyncOnChange) {
                start(c);
            } else {
                onChange(c);
            }
        },
        [asyncOnChange, onChange, start],
    );

    const handleItemChange = useCallback(
        (_column: IColumnChooser) => {
            const clone = arrayUtils.replaceItem(columns, _column.id, _column);

            onChange(clone);
        },
        [onChange, columns],
    );

    const handleSearch = useCallback((e: React.ChangeEvent<HTMLInputElement>) => {
        setSearch(e.target.value);
    }, []);

    return (
        <DropDown
            css={styles.root}
            className={className}
            placement="bottomRight"
            popup={
                <DropDownPopup>
                    <div tw="p-1">
                        <Input
                            type="text"
                            name="search"
                            tw="text-base"
                            prefix={<SearchOutlined />}
                            placeholder={t('Search columns')}
                            value={search}
                            onChange={handleSearch}
                        />
                    </div>
                    {filteredColumns.length ? (
                        <ColumnChooserList
                            onChange={handleChange}
                            items={filteredColumns}
                            renderItem={(item): JSX.Element => {
                                return <ColumnChooserItem column={item} onChange={handleItemChange} />;
                            }}
                        />
                    ) : (
                        <div tw="p-2 text-center">No matching results found</div>
                    )}
                    <div>
                        <Link tw="flex justify-center pt-1 text-base" name="resetToDefaults" onClick={onResetClick}>
                            <Icon icon={UndoOutlined} tw="pt-0.5 pr-1" />
                            <div>Reset to defaults</div>
                        </Link>
                    </div>
                </DropDownPopup>
            }
        >
            <Button css={styles.button} size="small" name="customizeTable" data-testid="customizeTableButton">
                <Icon icon={UnorderedListOutlined} tw="text-xl" />
            </Button>
        </DropDown>
    );
};

const styles = {
    root: css`
        background-color: ${THEME.GREEN};
        padding-right: 0px;
        width: 56px;
        .anticon-down {
            display: inline-block;
            svg {
                fill: white;
                color: white;
            }
        }
    `,
    button: css`
        border: 0;
        margin-top: 4px;
        background-color: transparent;
        svg {
            color: white;
        }
        :hover {
            background-color: transparent;
        }

        :focus {
            background-color: transparent;
        }
    `,
};
