import { ECouponStatus } from '@common/enums/coupon-status.enum';
import { StatusTag, TStatuses } from '@common/general-ui/tag/status-tag.component';
import React, { ReactNode } from 'react';

export type TCustomerStatus = 'active' | 'suspended';
export type TSubscriptionStatus = 'active' | 'future' | 'cancelled';
export type TPaymentStatus = 'paid' | 'pending' | 'payment_failed' | 'cancelled' | 'partial_refund' | 'refunded';

interface IProps {
    children: ReactNode;
    paymentStatus?: TPaymentStatus;
    orderStatus?: string;
    stockStatus?: string;
    taskStatus?: ETaskStatus;
    couponStatus?: ECouponStatus;
    customerStatus?: TCustomerStatus;
    subscriptionStatus?: TSubscriptionStatus;
}

export enum ETaskStatus {
    in_progress = 'in_progress',
    finished = 'finished',
    cancelled = 'cancelled',
    failed = 'failed',
}

const PAYMENT_STATUS_MAP: Record<TPaymentStatus, TStatuses> = {
    paid: 'success',
    pending: 'warning',
    payment_failed: 'error',
    cancelled: 'error',
    partial_refund: 'info',
    refunded: 'info',
};

const ORDER_STATUS_MAP: Record<string, TStatuses> = {
    queued: 'default',
};

const STOCK_STATUS_MAP: Record<string, TStatuses> = {
    available: 'success',
    running_out: 'warning',
    empty: 'error',
};

const TASK_STATUS_MAP: Record<ETaskStatus, TStatuses> = {
    [ETaskStatus.in_progress]: 'info',
    [ETaskStatus.finished]: 'success',
    [ETaskStatus.cancelled]: 'default',
    [ETaskStatus.failed]: 'error',
};

const COUPON_STATUS_MAP: Record<ECouponStatus, TStatuses> = {
    [ECouponStatus.active]: 'success',
    [ECouponStatus.expired]: 'error',
    [ECouponStatus.archived]: 'warning',
};

const CUSTOMER_STATUS: Record<TCustomerStatus, TStatuses> = {
    active: 'success',
    suspended: 'warning',
};

const SUBSCRIPTION_STATUS: Record<TSubscriptionStatus, TStatuses> = {
    active: 'success',
    cancelled: 'warning',
    future: 'info',
};

export const CustomStatusTag = (props: IProps): JSX.Element => {
    const {
        paymentStatus,
        orderStatus,
        stockStatus,
        taskStatus,
        customerStatus,
        subscriptionStatus,
        couponStatus,
        ...rest
    } = props;
    if (paymentStatus) {
        return (
            <StatusTag {...rest} status={PAYMENT_STATUS_MAP[paymentStatus]}>
                {props.children}
            </StatusTag>
        );
    }

    if (orderStatus) {
        return (
            <StatusTag {...rest} status={ORDER_STATUS_MAP[orderStatus]}>
                {props.children}
            </StatusTag>
        );
    }

    if (stockStatus) {
        return (
            <StatusTag {...rest} status={STOCK_STATUS_MAP[stockStatus]}>
                {props.children}
            </StatusTag>
        );
    }

    if (taskStatus) {
        return (
            <StatusTag {...rest} status={TASK_STATUS_MAP[taskStatus]}>
                {props.children}
            </StatusTag>
        );
    }

    if (couponStatus) {
        return (
            <StatusTag {...rest} status={COUPON_STATUS_MAP[couponStatus]}>
                {props.children}
            </StatusTag>
        );
    }

    if (customerStatus) {
        return (
            <StatusTag {...rest} status={CUSTOMER_STATUS[customerStatus]}>
                {props.children}
            </StatusTag>
        );
    }

    if (subscriptionStatus) {
        return (
            <StatusTag {...rest} status={SUBSCRIPTION_STATUS[subscriptionStatus]}>
                {props.children}
            </StatusTag>
        );
    }

    return <></>;
};
