import dayjs from 'dayjs';

export const equalsByJSON = (arg: unknown, arg2: unknown): boolean => {
    return JSON.stringify(arg) === JSON.stringify(arg2);
};

export function equals<T>(arg1: T, arg2: T): boolean {
    if (
        arg1 &&
        (arg1 as unknown as { getTime: () => number }).getTime &&
        dayjs(arg1 as unknown as string).isSame(arg2 as unknown as string)
    ) {
        return true;
    }
    if (arg1 && (typeof arg1).toLowerCase() === 'object' && Object.keys(arg1).length) {
        return !Object.keys(arg1).some((key) => !equals(arg1 ? arg1[key] : undefined, arg2 ? arg2[key] : undefined));
    }
    if (arg1 && Array.isArray(arg1)) {
        if (arg1.length == (arg2 as unknown as Array<T>)?.length && arg1.length === 0) {
            return true;
        } else if (arg1.length !== (arg2 as unknown as Array<T>)?.length) {
            return false;
        }
        return arg1.some((_, idx) => !equals(arg1 ? arg1[idx] : undefined, arg2 ? arg2[idx] : undefined));
    }
    return arg1 === arg2;
}
