import { ITableUISettings } from '@common/interfaces/ui-settings.interface';
import { localStorageService } from '@common/services/local-storage.service';
import { useMemo, useState } from 'react';

interface IProps {
    initial?: ITableUISettings;
    name: string;
}

interface IResult {
    tableUISettings: ITableUISettings;
    saveTableUISettings: (data: ITableUISettings) => void;
}

const KEY = `ui-settings`;

export function useTableUISettings({ name, initial = { chooserColumns: [] } }: IProps): IResult {
    const fromStorage = localStorageService.load<ITableUISettings>(KEY, { chooserColumns: [] });
    const [ui, setUI] = useState<ITableUISettings>(fromStorage && fromStorage[name] ? fromStorage[name] : initial);

    return useMemo((): IResult => {
        return {
            tableUISettings: ui,
            saveTableUISettings: (data: ITableUISettings): void => {
                const prev = localStorageService.load(KEY, {});

                setUI(data);
                localStorageService.save(KEY, {
                    ...prev,
                    [name]: data,
                });
            },
        };
    }, [name, ui]);
}
