import { arrayUtils } from '@common/utils/array-utils.util';
import React, { useEffect, useState } from 'react';
import { DragDropContext, Draggable, DraggingStyle, Droppable, DropResult, NotDraggingStyle } from 'react-beautiful-dnd';
import { css } from 'twin.macro';
import { IColumnChooser } from './column-chooser.component';

interface IProps {
    items: IColumnChooser[];
    renderItem: (item: IColumnChooser) => JSX.Element;
    onChange: (newColumns: IColumnChooser[]) => void;
}

export const ColumnChooserList = ({ items: appItems, renderItem, onChange }: IProps): JSX.Element => {
    const [items, setItems] = useState(appItems);

    useEffect(() => {
        setItems(appItems);
    }, [appItems]);

    const handleDragEnd = (result: DropResult): void => {
        if (!result.destination) {
            return;
        }
        const newItems = arrayUtils.reorder(items, result.source.index, result.destination.index);
        onChange(newItems);
        setItems(newItems);
    };

    return (
        <DragDropContext onDragEnd={handleDragEnd}>
            <Droppable droppableId="droppable">
                {(provided): JSX.Element => (
                    <div
                        css={styles.root}
                        {...provided.droppableProps}
                        ref={provided.innerRef}
                        // style={getListStyle(snapshot.isDraggingOver)}
                    >
                        {items.map((item, index) => (
                            <Draggable key={item.id} draggableId={item.id.toString()} index={index}>
                                {(provided, snapshot): JSX.Element => (
                                    <div
                                        ref={provided.innerRef}
                                        {...provided.draggableProps}
                                        {...provided.dragHandleProps}
                                        style={getItemStyle(snapshot.isDragging, provided.draggableProps.style)}
                                    >
                                        {renderItem(item)}
                                    </div>
                                )}
                            </Draggable>
                        ))}
                        {provided.placeholder}
                    </div>
                )}
            </Droppable>
        </DragDropContext>
    );
};
const getItemStyle = (
    isDragging: boolean,
    draggableStyle: DraggingStyle | NotDraggingStyle | undefined,
): React.CSSProperties => ({
    userSelect: 'none',

    background: isDragging ? '#efefef' : 'transparent',

    ...draggableStyle,
});

// TODO remove when customization will ready - (example)
// const getListStyle = (isDraggingOver: boolean) => ({
//     background: isDraggingOver ? 'lightblue' : 'lightgrey',
//     padding: grid,
//     width: 250,
// });

const styles = {
    root: css`
        display: flex;
        flex-direction: column;
        height: 196x;
        max-height: 196x;
        overflow-y: auto;
    `,
};
