import { IDictionary } from '@common/interfaces/dictionary.interface';

export function removeSame<T>(obj1: T, obj2: T): T {
    return removeSameInternal(obj1, obj2);
}

function removeSameInternal<T>(obj1: T, obj2: T): T {
    if (obj1) {
        if (Array.isArray(obj1) && Array.isArray(obj2)) {
            if (obj1.length === obj2.length) {
                return obj1
                    .map((val1, index) => {
                        return removeSameInternal(val1, obj2 ? obj2[index] : undefined);
                    })
                    .filter((f) => f !== undefined) as unknown as T;
            } else {
                return obj2
                    .map((val2, index) => {
                        return removeSameInternal(obj1 ? obj1[index] : undefined, val2);
                    })
                    .filter((f) => f !== undefined) as unknown as T;
            }
        }

        const keys = Object.keys(obj1);
        const res = {} as IDictionary;
        keys.forEach((key) => {
            const val1 = obj1 ? obj1[key] : undefined;
            const val2 = obj2 ? obj2[key] : undefined;
            if (
                (typeof val1 === 'object' && val1 !== null && Object.keys(val1).length > 0) ||
                (typeof val2 === 'object' && val2 !== null && Object.keys(val2).length > 0)
            ) {
                res[key] = removeSameInternal(val1, val2);
            } else if (val1 !== val2) {
                res[key] = val2;
            }
        });

        return Object.keys(res).length == 0 ? (undefined as unknown as T) : (res as unknown as T);
    }
    return undefined as unknown as T;
}
