import { notify } from '@common/general-ui/notify/notify.component';
import { t } from '@common/hooks/use-t.hook';
import { IBEError } from '@common/interfaces/be-error.interface';

let _errorHandler: (error: IBEError) => void;
export const customNotify = {
    initCustomHandler: (errorHandler: (error: IBEError) => void): void => {
        _errorHandler = errorHandler;
    },

    // eslint-disable-next-line @typescript-eslint/explicit-module-boundary-types
    error: (e: IBEError<unknown> | (IBEError<unknown> | undefined | null)[] | null): void => {
        if (Array.isArray(e)) {
            e.filter((f) => f).forEach((e) => customNotify.error(e as IBEError));
        } else if (e) {
            const beError = e as IBEError<string> | undefined;
            if (beError?.error) {
                _errorHandler(beError);
            } else {
                notify.error({
                    title: t('Operation failed'),
                    description: t('Something went wrong'),
                });
            }
        } else {
            notify.error({
                title: t('Operation failed'),
                description: t('Something went wrong'),
            });
        }
    },
};
