/* eslint-disable @typescript-eslint/no-explicit-any */
import { IS_DEBUG } from '@common/constants/is-debug.constant';
import { InlineJsonPreview } from '@common/general-ui/json-preview/json-preview.component';
import { IUseCustomFormReturn } from '@common/hooks/use-form.hook';
import React, { ChangeEventHandler, ReactNode, useCallback, useMemo } from 'react';
import { FormProvider, UnpackNestedValue } from 'react-hook-form';

// For some components need trigger validate event manually, so added additional context.
export interface IFormAdditionalContextProps<T> {
    registerBlurHandler: (name: keyof T, handler: ChangeEventHandler) => void;
    onFieldBlur: (name: keyof T) => void;
    onFieldChange: (name: keyof T) => void;
}

export const FormAdditionalContext = React.createContext<IFormAdditionalContextProps<unknown>>({
    registerBlurHandler: () => null,
    onFieldBlur: () => null,
    onFieldChange: () => null,
});

interface IProps<T> {
    form: IUseCustomFormReturn<T>;
    children: ReactNode;
    className?: string;
}

export function Form<T>({
    children,
    form: {
        formContext,
        isValid,
        props: { onFieldBlur, onFieldChange, onSubmit },
        _onBlurHandlers,
    },
    className,
}: IProps<T>): JSX.Element {
    const handleSubmit = useCallback(
        (data: UnpackNestedValue<T>) => {
            // eslint-disable-next-line @typescript-eslint/no-explicit-any
            onSubmit(data as any);
        },
        [onSubmit],
    );

    const renderJsonPreview = (): JSX.Element => {
        const json = {
            values: formContext.getValues(),
            errors: formContext.formState.errors,
            touched: formContext.formState.touchedFields,
            formState: formContext.formState,
            isValid,
        };
        return <InlineJsonPreview json={json} collapsed_json={false} />;
    };

    const registerBlurHandler = useCallback(
        (fieldName: keyof T, handler: ChangeEventHandler) => {
            _onBlurHandlers[fieldName as string] = handler;
        },
        [_onBlurHandlers],
    );

    const onFieldBlurHandler = useCallback(
        (fieldName: keyof T) => {
            onFieldBlur && onFieldBlur(fieldName, formContext.getValues() as T);
        },
        [onFieldBlur, formContext],
    );

    const onFieldChangeHandler = useCallback(
        (fieldName: keyof T) => {
            onFieldChange && onFieldChange(fieldName, formContext.getValues() as T);
        },
        [formContext, onFieldChange],
    );

    const additionalContext: IFormAdditionalContextProps<T> = useMemo(() => {
        return {
            registerBlurHandler,
            onFieldBlur: onFieldBlurHandler,
            onFieldChange: onFieldChangeHandler,
        };
    }, [onFieldBlurHandler, onFieldChangeHandler, registerBlurHandler]);

    return (
        <FormProvider {...(formContext as any)}>
            <FormAdditionalContext.Provider value={additionalContext}>
                {IS_DEBUG && renderJsonPreview()}
                <form autoComplete="off" className={className} onSubmit={formContext.handleSubmit(handleSubmit as any)}>
                    {children}
                </form>
            </FormAdditionalContext.Provider>
        </FormProvider>
    );
}
