import { formatPrice } from '@common/utils/format-price.util';
import React from 'react';
import 'twin.macro';

interface IProps {
    boxPrice: number | undefined;
    totalAddonsPrice: number | undefined;
    shippingPrice: number | undefined;
    totalPrice: number | undefined;
    coupon: string | undefined;
    couponLabel: string | undefined;
    addonNames?: string;
    className?: string;
}
export const PriceInfo = ({
    boxPrice,
    shippingPrice,
    coupon,
    couponLabel,
    totalPrice,
    addonNames,
    className,
    totalAddonsPrice,
}: IProps): JSX.Element => {
    return (
        <div className={className}>
            {boxPrice && boxPrice > 0 ? (
                <div tw="flex justify-between">
                    <span tw="font-normal">Box:&nbsp;</span> <span>{formatPrice(boxPrice || 0)}</span>
                </div>
            ) : undefined}
            {totalAddonsPrice && totalAddonsPrice > 0 ? (
                <div tw="flex justify-between">
                    <span tw="font-normal">Addons{addonNames ? ` (${addonNames})` : ''}:&nbsp;</span>
                    <span>{formatPrice(totalAddonsPrice || 0)}</span>
                </div>
            ) : undefined}
            {shippingPrice && shippingPrice > 0 ? (
                <div tw="flex justify-between">
                    <span tw="font-normal">Shipping:&nbsp;</span> <span>{formatPrice(shippingPrice || 0)}</span>
                </div>
            ) : undefined}
            {coupon ? (
                <div tw="flex justify-between">
                    <span tw="font-normal">{couponLabel}</span>
                    <span>{coupon}</span>
                </div>
            ) : undefined}
            <div tw="flex justify-between">
                <span tw="font-normal">Total:&nbsp;</span> <span>{formatPrice(totalPrice || 0)}</span>
            </div>
        </div>
    );
};
