import { useField } from '@common/hooks/use-field.hook';
import React, { useCallback } from 'react';
import { Checkbox, ICheckboxChangeHandler, ICheckboxProps } from './checkbox.component';

export function FormCheckbox<TFormValues>(
    props: Omit<ICheckboxProps<keyof TFormValues>, 'value' | 'defaultValue'>,
): JSX.Element {
    const { onChange, onBlur } = props;
    const { setFieldValue, setFieldTouched, value, errorMessage, triggerValidate } = useField<boolean>({
        ...props,
        getCustomErrorMessage: props.getCustomErrorMessage,
    });

    const handleChange = useCallback<ICheckboxChangeHandler>(
        (e) => {
            onChange && onChange(e);
            setFieldValue(e.checked);
            setFieldTouched();
            triggerValidate();
        },
        [onChange, setFieldValue, setFieldTouched, triggerValidate],
    );

    const handleBlur = useCallback(
        (e: React.FocusEvent<HTMLInputElement>) => {
            onBlur && onBlur(e);
            setFieldTouched();
        },
        [onBlur, setFieldTouched],
    );

    return (
        <Checkbox
            errorMessage={errorMessage}
            {...props}
            onChange={handleChange}
            onBlur={handleBlur}
            name={props.name as string}
            value={value}
        />
    );
}
