import { THEME } from '@common/constants/theme.constant';
import { SerializedStyles } from '@emotion/react';
import { Collapse as AntdCollapse, CollapseProps } from 'antd';
import React, { ReactNode } from 'react';
import { css } from 'twin.macro';

const { Panel: AntdPanel } = AntdCollapse;

export interface ICollapseItem {
    component: React.FC;
    name: string;
    title: ReactNode;
}

export const CollapsePanel = AntdPanel;

interface IProps {
    defaultActiveKey?: string;
    items?: ICollapseItem[];
    children?: ReactNode;
    accordion?: boolean | undefined;
    onChange?: CollapseProps['onChange'];
    ghost?: CollapseProps['ghost'];
}

export const Collapse = ({ items, ghost, accordion, children, defaultActiveKey, onChange }: IProps): JSX.Element => {
    const renderHeader = (title: ReactNode): ReactNode => {
        return title;
    };
    return (
        <AntdCollapse
            ghost={ghost}
            accordion={accordion}
            onChange={onChange}
            css={styles.root(ghost)}
            defaultActiveKey={defaultActiveKey || (items && items[0] ? [items[0].name] : undefined)}
        >
            {items?.map((item) => {
                return (
                    <AntdPanel header={renderHeader(item.title)} key={item.name}>
                        <item.component />
                    </AntdPanel>
                );
            })}
            {children}
        </AntdCollapse>
    );
};

const styles = {
    root: (ghost: boolean | undefined): (SerializedStyles | boolean)[] => [
        css`
            margin-bottom: 10px;
        `,
        ghost
            ? css`
                  && .ant-collapse-arrow {
                  }
              `
            : css`
                  && .ant-collapse-header {
                      background-color: ${THEME.GREEN};
                      color: white;
                      font-weight: 200;
                      display: flex;
                      flex-shrink: 0;
                      width: 100%;

                      .ant-collapse-arrow {
                          padding-top: 6px;
                      }
                  }
              `,
    ],
};
